import React, { useEffect } from 'react';
import { useParams } from 'react-router';
import { Link } from 'react-router-dom';
import { useDispatch, useSelector } from 'react-redux';
import { getDogsDetail } from '../../actions';


export const Detail = () => {

  const { id } = useParams();
  const dispatch = useDispatch();
  const dogsDetail = useSelector( (state) => state.DogDetail);

  useEffect( () => {
    dispatch(getDogsDetail(id));
  },[dispatch,id]);

  return (
    <div>
      <Link to='/home'>
        <button>Home</button>
      </Link>
      {
        dogsDetail && (
          <div>
            <h1>{dogsDetail[0].name}</h1>
              <br />
              <img src={dogsDetail[0].img ? dogsDetail[0].img : dogsDetail[0].image} alt='Not found' width='200px' height='250px' />
                <h2>Height :</h2>
                  <p>{dogsDetail[0].height_min}</p>
                  <p>{dogsDetail[0].height_max} centimeters</p>
                <h2>Weight :</h2>
                  <p>{dogsDetail[0].weight_min}</p>
                  <p>{dogsDetail[0].weight_max} kilos</p>
                <h2>Life Span</h2>
                  <p>{dogsDetail[0].life_time_min}</p>
                  <p>{dogsDetail[0].life_time_max} years</p>
                <h2>Temperaments</h2>
                  <p>{!dogsDetail[0].createInDb ? dogsDetail[0].temperament : dogsDetail[0].Temperaments.map( (e) => e.name + " ")}</p>
          </div>
        )
      }
    </div>
  )
};
