import { GET_DOGS , GET_DOGS_NAME , GET_DOGS_BY_TEMPERAMENT } from '../actions';

const initialState = {
  dogs: [],
  AllDogs: []
};

const RootReducer = ( state = initialState , action ) => {

  switch (action.type) {
    case GET_DOGS:
      return {
        ...state,
        dogs: action.payload,
        AllDogs: action.payload
      }

    case GET_DOGS_NAME:
      return {
        ...state,
        dogs: action.payload
      }

    case GET_DOGS_BY_TEMPERAMENT:
      const AllDogs= state.AllDogs;
      const DogsFilterDB = action.payload === 'All' ? AllDogs : AllDogs.filter( (el) => el.temperament.includes(action.payload) );
      return {
        ...state,
        dogs: DogsFilterDB,
      }
    default:
      return state;

  }
};

export default RootReducer ;
