import axios from 'axios';

export const GET_DOGS = 'GET_DOGS';
export const GET_DOGS_NAME = 'GET_DOGS_NAME';
export const GET_DOGS_BY_TEMPERAMENT = 'GET_DOGS_BY_TEMPERAMENT';
export const GET_DOGS_BY_WEIGTH = 'GET_DOGS_BY_WEIGTH';


export const getDogs = () => {
  return (dispatch) => {
      axios.get('http://localhost:3004/dogs')
      .then( (res) => {
        dispatch({
          type: GET_DOGS,
          payload: res.data,
        });
      })
      .catch( (err) => console.log(err));
  }
};

export const getDogsName = (name) => {
   return (dispatch) => {
      axios.get(`http://localhost:3004/dogs?name=${name}`)
      .then( (res) => {
        dispatch({
          type: GET_DOGS_NAME,
          payload: res.data,
        })
      })
      .catch( (err) => console.log(err));
    }
};

export const getDogsByTemperament = (payload) => {
  return {
        type: GET_DOGS_BY_TEMPERAMENT,
        payload
      }
};
