import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { useDispatch, useSelector } from 'react-redux';
import { getTemperaments , postCreateDog } from '../../actions';

const  validate = (input) => {
    const errors = {};

    if (!input.name){
        errors.name = 'Se requiere un nombre';
    }
    if(!input.height_min){
        errors.height_min = 'Se requiere una altura maxima';
    }
    if(!input.height_max){
        errors.height_max = 'Se requiere una altura minima';
    }
    if(input.height_max <= input.height_min){
        errors.height_min = 'Min no puede ser Mayor o Igual que Max';
    }
    if(!input.weight_min){
        errors.weight_min ='Se requiere un peso minimo';
    }
    if(!input.weight_max){
        errors.weight_max = 'Se requiere un peso maxima';
    }
    if(input.weight_max <= input.weight_min){
        errors.weight_min = 'Min no puede ser Mayor o Igual que Max';
    }
    if(!input.life_time_min){
        errors.life_time_min = 'Se requiere una esperanza de vida minima';
    }
    if(!input.life_time_max){
    errors.life_time_max = 'Se requiere una esperanza de vida  maxima';
    }
    if(input.life_time_max <= input.life_time_min){
        errors.life_time_min = 'Min no puede ser Mayor o Igual que Max';
    }
    if(!input.imga ){
        errors.imga = 'Se requiere una URL';
    }
    if(!input.temperament.length){
        errors.temperament = 'Se requieren  Temperamentos';
    }
    return errors;
}

export const CreateDog = () => {

    const dispatch = useDispatch()
    const navigate = useNavigate();
    const temperaments = useSelector((state)=> state.temperaments)

    const[input, setInput] = useState({
        name: "",
        height_min: "",
        height_max: "",
        weight_min: "",
        weight_max: "",
        life_time_min: "",
        life_time_max: "",
        temperament:[],
        image:"",
    })
    const [errors, setErros] = useState({});



    useEffect(() => {
        dispatch(getTemperaments());
    }, [dispatch]);

    const handleChangeInput = (e) => {
        setInput({
            ...input,
            [e.target.name]: e.target.value
        });
        setErros(validate({
            ...input,
            [e.target.name]: e.target.value
        }));
        console.log(input);
    };

    const handleSelect = (e) => {
        setInput({
            ...input,
            temperament: [...input.temperament,e.target.value]
        });
    };


    const handleDelete = (el) => {
        setInput({
            ...input,
            temperament: input.temperament.filter(e=> e !== el)
        })
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        dispatch(postCreateDog(input));
        console.log(input);
        alert('dog created');
        setInput({
            name: "",
            height_min: "",
            height_max: "",
            weight_min: "",
            weight_max: "",
            life_time_min: "",
            life_time_max: "",
            temperament:[],
            image:"",
        })
        navigate('/home')
    };

    return(
        <div>
            <Link to='/home'>
                <button>Home</button>
            </Link>
            <form onSubmit={ (e) => handleSubmit(e)}>
                <div>
                    <label>Name:</label>
                    <input
                        type='text'
                        value={input.name}
                        onChange={ (e) => handleChangeInput(e)}
                        name='name'
                    />
                    {errors.name && (<p>{errors.name}</p>)}
                </div>
                <div>
                    <label>height_min:</label>
                    <input
                        type='text'
                        value={input.height_min}
                        onChange={ (e) => handleChangeInput(e)}
                        name='height_min'
                    />
                    {errors.height_min && (<p>{errors.height_min}</p>)}
                </div>
                <div>
                    <label>height_max:</label>
                    <input
                        type='text'
                        value={input.height_max}
                        onChange={ (e) => handleChangeInput(e)}
                        name='height_max'
                    />
                    {errors.height_max && (<p>{errors.height_max}</p>)}
                </div>
                <div>
                    <label>weight_min:</label>
                    <input
                        type='text'
                        value={input.weight_min}
                        onChange={ (e) => handleChangeInput(e)}
                        name='weight_min'
                    />
                    {errors.weight_min && (<p>{errors.weight_min}</p>)}
                </div>
                <div>
                    <label>weight_max:</label>
                    <input
                        type='text'
                        value={input.weight_max}
                        onChange={ (e) => handleChangeInput(e)}
                        name='weight_max'
                    />
                    {errors.weight_max && (<p>{errors.weight_max}</p>)}
                </div>
                <div>
                    <label>life_time_min:</label>
                    <input
                        type='text'
                        value={input.life_time_min}
                        onChange={ (e) => handleChangeInput(e)}
                        name='life_time_min'
                    />
                    {errors.life_time_min && (<p>{errors.life_time_min}</p>)}
                </div>
                <div>
                    <label>life_time_max:</label>
                    <input
                        type='text'
                        value={input.life_time_max}
                        onChange={ (e) => handleChangeInput(e)}
                        name='life_time_max'
                    />
                    {errors.life_time_max && (<p>{errors.life_time_max}</p>)}
                </div>
                <div>
                    <label>image:</label>
                    <input
                        type='text'
                        value={input.image}
                        onChange={ (e) => handleChangeInput(e)}
                        name='image'
                    />
                    {errors.image && (<p>{errors.image}</p>)}
                </div>
                <div>
                    <label>temperament:</label>
                    {errors.temperament && (<p>{errors.temperament}</p>)}
                    <select onChange={ (e) => handleSelect(e)}>
                        {
                            temperaments.map( (res) => (
                                <option value={res.name} key={res.id}>{res.name}</option>
                            ))
                        }
                    </select>
                    <ul>
                        <li>
                            {
                                input.temperament.map( (el) =>
                                    <div>
                                        <p>{el}</p>
                                        <button type='button' key={el.id} onClick={()=>handleDelete(el)}>X</button>
                                    </div>
                                )
                            }
                        </li>
                    </ul>
                </div>
                 <button type='submit'>Create Dog</button>
            </form>
        </div>
    )
}
