import { GET_DOGS , GET_DOGS_NAME , FILTER_DOGS_BY_TEMPERAMENT , ORDER_DOGS_BY_WEIGTH } from '../actions';

const initialState = {
  dogs: [],
  AllDogs: []
};

const RootReducer = ( state = initialState , action ) => {

  switch (action.type) {
    case GET_DOGS:
      return {
        ...state,
        dogs: action.payload,
        AllDogs: action.payload
      }

    case GET_DOGS_NAME:
      return {
        ...state,
        dogs: action.payload
      }

    case FILTER_DOGS_BY_TEMPERAMENT:
      const AllDogs= state.AllDogs;
      const DogsFilterDB = action.payload === 'All' ? AllDogs : AllDogs.filter( (el) => el.temperament.includes(action.payload) );
      return {
        ...state,
        dogs: DogsFilterDB,
      }

    case ORDER_DOGS_BY_WEIGTH:
      const sortDogs = action.payload === 'weigthAsc' ?
      state.dogs.sort(function(a,b){

        return b.weight_min - a.weight_min;
      }) :

      state.dogs.sort(function(a,b){

        return a.weight_min - b.weight_min;
      })
        return {
          ...state,
          dogs: sortDogs
        }

    default:
      return state;

  }
};

export default RootReducer ;
