import React, { useEffect } from 'react';
import { useParams } from 'react-router';
import { Link } from 'react-router-dom';
import { useDispatch, useSelector } from 'react-redux';
import { getDogsDetail } from '../../actions';


export const Detail = () => {

  const { id } = useParams();
  const dispatch = useDispatch();
  const dogDetail = useSelector( (state) => state.dogDetail);

  useEffect( () => {
    dispatch(getDogsDetail(id));
  },[dispatch,id]);

  return (
    <div>
      <Link to='/home'>
        <button>Home</button>
      </Link>
      {
        <div>
          <h1>{dogDetail.name}</h1>
          <br />
          <img src={dogDetail.img ? dogDetail.img : dogDetail.image} alt='Not found' width='200px' height='250px' />
          <h2>Height :</h2>
          <p>{dogDetail.height_min}</p>
          <p>{dogDetail.height_max} centimeters</p>
          <h2>Weight :</h2>
          <p>{dogDetail.weight_min}</p>
          <p>{dogDetail.weight_max} kilos</p>
          <h2>Life Span</h2>
          <p>{dogDetail.life_time_min}</p>
          <p>{dogDetail.life_time_max} years</p>
          <h2>Temperaments</h2>
          <p>{!dogDetail.createInDb ? dogDetail.temperament : dogDetail.Temperaments.map( (e) => e.name + " ")}</p>
        </div>
      }
    </div>
  )
};
