import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { useDispatch, useSelector } from 'react-redux';
import { getTemperaments , postCreateDog } from '../../actions';


export const CreateDog = () => {

    const dispatch = useDispatch()
    const temperaments = useSelector((state)=> state.temperaments)

    const[input, setInput] = useState({
        name: "",
        height_min: "",
        height_max: "",
        weight_min: "",
        weight_max: "",
        life_time_min: "",
        life_time_max: "",
        temperament:[],
        image:"",
    })

    useEffect(() => {
        dispatch(getTemperaments());
    }, [dispatch]);

    const handleChangeName = (e) => {
        setInput({
            ...input,
            [e.target.name]: e.target.value
        })
    };

    const handleSelect = (e) => {
        setInput({
            ...input,
            temperament: [...input.temperament,e.target.value]
        })
    };


    function handleDelete(el){
        setInput({
            ...input,
            temperament: input.temperament.filter(e=> e !== el)
        })
    }

    return(
        <div>
            <Link to='/home'>
                <button>Home</button>
            </Link>
            <form>
                <div>
                    <label>Name:</label>
                    <input
                        type='text'
                        value={input.name}
                        onChange={ (e) => handleChangeName(e)}
                        name='name'
                    />
                </div>
                <div>
                    <label>height_min:</label>
                    <input
                        type='text'
                        value={input.height_min}
                        onChange={ (e) => handleChangeName(e)}
                        name='height_min'
                    />
                </div>
                <div>
                    <label>height_max:</label>
                    <input
                        type='text'
                        value={input.height_max}
                        onChange={ (e) => handleChangeName(e)}
                        name='height_max'
                    />
                </div>
                <div>
                    <label>weight_min:</label>
                    <input
                        type='text'
                        value={input.weight_min}
                        onChange={ (e) => handleChangeName(e)}
                        name='weight_min'
                    />
                </div>
                <div>
                    <label>weight_max:</label>
                    <input
                        type='text'
                        value={input.weight_max}
                        onChange={ (e) => handleChangeName(e)}
                        name='weight_max'
                    />
                </div>
                <div>
                    <label>life_time_min:</label>
                    <input
                        type='text'
                        value={input.life_time_min}
                        onChange={ (e) => handleChangeName(e)}
                        name='life_time_min'
                    />
                </div>
                <div>
                    <label>life_time_max:</label>
                    <input
                        type='text'
                        value={input.life_time_max}
                        onChange={ (e) => handleChangeName(e)}
                        name='nalife_time_maxme'
                    />
                </div>
                <div>
                    <label>image:</label>
                    <input
                        type='text'
                        value={input.image}
                        onChange={ (e) => handleChangeName(e)}
                        name='image'
                    />
                </div>
                <div>
                    <label>temperament:</label>
                    <select onChange={ (e) => handleSelect(e)}>
                        {
                            temperaments.map( (res) => (
                                <option value={res.name} key={res.id}>{res.name}</option>
                            ))
                        }
                    </select>
                    <ul>
                        <li>
                            {
                                input.temperament.map( (el) =>
                                <button type='button' key={el.id} onClick={()=>handleDelete(el)}>{el}</button> )
                            }
                        </li>
                    </ul>
                </div>
                 <button type='submit'>Create Dog</button>
            </form>
        </div>
    )
}
